def json_subset(key_1, key_2, jsn):
    js_sub = {}
    keys = [key_1, key_2]
    for key, value in json.loads(str(jsn)).items():
        for i in keys:
            if i == key:
                js_sub.update({i:value})
    print(json.dumps(js_sub))
    return json.dumps(js_sub)

